import React, { useState } from 'react';
import {
  ImageBackground,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  Image,
  FlatList,
} from 'react-native';

import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { SafeAreaView } from 'react-native-safe-area-context';
import Carousel, { Pagination } from 'react-native-snap-carousel';

import { SLIDER_WIDTH, ITEM_WIDTH } from '../code/carouselCardItem';
import { decks } from '../code/data';
import Flashcard from '../code/flashcards';
import CarouselCards from '../code/carouselCards';

const App = ({ navigation }) => {
  const [selectedDeck, setSelectedDeck] = useState(decks[0]);
  const [index, setIndex] = React.useState(0);
  const isCarousel = React.useRef(null);

  const handleDeckPress = (deck) => {
    setSelectedDeck(deck);
  };

  return (
    <ImageBackground
      source={require('../assets/JungleBg.gif')}
      style={styles.backgroundImage}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <View style={styles.container}>
          <Text style={[styles.l1, { top: 10 }]}>Hello, there!</Text>
          <Text style={[styles.l2, { top: 10 }]}>
            What do you want to learn today?
          </Text>

          <View style={[styles.c2]}>
            <Text style={styles.c2l1} numberOfLines={2}>
              Plant seeds of wisdom {'\n'}one flashcard at a time!
            </Text>

            <TouchableOpacity style={styles.c2l2}>
              <Text style={{ color: '#ECE3CE' }}>Create a study set now!</Text>
            </TouchableOpacity>
          </View>

          <Image
            style={styles.homechar}
            source={require('../assets/home_char.png')}
          />

          <View style={styles.flashcardcontainer} id="recent">
            <Text style={[styles.l3, { top: 10 }]}>Recently Viewed</Text>
            <View>
              <FlatList
                data={decks}
                horizontal
                keyExtractor={(item) => item.id.toString()}
                renderItem={({ item }) => (
                  <TouchableOpacity
                    onPress={() => handleDeckPress(item)}
                    style={styles.deckContainer}>
                    <Text style={styles.deckTitle}>{item.name}</Text>
                  </TouchableOpacity>
                )}
              />
              <TouchableOpacity>
                <Text onPress={() => navigation.navigate('Create')}>ADD DECK</Text>
              </TouchableOpacity>
            </View>
            <Text style={styles.deckTitle}>{selectedDeck.name}</Text>
            <Carousel
              layout="default"
              layoutCardOffset={9}
              ref={isCarousel}
              data={selectedDeck.flashcards}
              renderItem={({ item }) => (
                <Flashcard
                  route={{
                    params: {
                      frontContent: item.frontContent,
                      backContent: item.backContent,
                    },
                  }}
                />
              )}
              sliderWidth={SLIDER_WIDTH}
              itemWidth={ITEM_WIDTH}
              onSnapToItem={(index) => setIndex(index)}
              useScrollView={true}
            />
            {/* Move the Flashcard component outside the Carousel */}
            <Flashcard
              route={{
                params: {
                  frontContent:
                    selectedDeck.flashcards[index].frontContent,
                  backContent:
                    selectedDeck.flashcards[index].backContent,
                },
              }}
            />
          </View>
        </View>
      </ScrollView>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  scrollContainer: {
    flexGrow: 1,
    backgroundColor: 'rgba(0, 0, 0, 0)',
    alignItems: 'center',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
  },
  l1: {
    fontSize: 20,
    color: '#FFFFFF',
    marginBottom: 5,
  },
  l2: {
    fontSize: 12,
    marginBottom: 15,
    color: '#FFFFFF',
  },
  l3: {
    fontSize: 14,
    color: '#ECE3CE',
    marginLeft: -20,
    marginBottom: 5,
  },
  c2: {
    backgroundColor: '#ECE3CE',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10,
    padding: 20,
    position: 'relative',
    borderRadius: 20,
    opacity: 0.8,
  },
  c2l1: {
    fontSize: 16,
    marginRight: 140,
  },
  c2l2: {
    backgroundColor: '#4F6F52',
    paddingHorizontal: 10,
    paddingVertical: 10,
    borderRadius: 5,
    marginTop: 10,
    alignSelf: 'flex-start',
    borderRadius: 20,
    left: -5,
  },
  homechar: {
    width: 200,
    height: 220,
    position: 'absolute',
    top: 4,
    left: 180,
  },
  flashcardcontainer: {
    flex: 1,
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'flex-start',
    padding: 20,
  },
  deckTitle: {
    marginVertical: 15,
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  addButton: {
    marginTop: 20,
    padding: 10,
    backgroundColor: 'green',
    borderRadius: '100%',
  },
});

export default App;
