import React, { useState, useRef } from 'react';
import {
  ImageBackground,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  Image,
  FlatList,
} from 'react-native';
import Carousel, { Pagination } from 'react-native-snap-carousel';

import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { SafeAreaView } from 'react-native-safe-area-context';

import { SLIDER_WIDTH, ITEM_WIDTH } from '../code/carouselCardItem';
import { decks } from '../code/data';
import Flashcard from '../code/flashcards';
import CarouselCards from '../code/carouselCards';

const App = () => {
  const [selectedDeck, setSelectedDeck] = useState(decks[0]);
  const [index, setIndex] = useState(0);
  const isCarousel = useRef(null);

  const handleDeckPress = (deck, index) => {
    setSelectedDeck(deck);
    isCarousel.current.snapToItem(index);
  };

  const handleButtonPress = () => {
    navigation.navigate('AnotherScreen');
  };

  const renderItem = ({ item, index }) => (
    <TouchableOpacity
      onPress={() => handleDeckPress(item, index)}
      style={styles.deckContainer}
    >
      <Text style={styles.deckTitle}>{item.name}</Text>
    </TouchableOpacity>
  );

  return (
    <ImageBackground
      source={require('../assets/JungleBg.gif')}
      style={styles.backgroundImage}
    >
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <View style={styles.container}>
          {/* ... Your existing code ... */}

          <View style={styles.flashcardcontainer} id='recent'>
            <Text style={[styles.l3, { top: 10 }]}>Recently Viewed</Text>
            <Carousel
              ref={isCarousel}
              data={decks}
              renderItem={renderItem}
              sliderWidth={SLIDER_WIDTH}
              itemWidth={ITEM_WIDTH}
              onSnapToItem={(index) => setIndex(index)}
            />
            <TouchableOpacity style={styles.addButton}>
              <Text style={styles.addButtonText}>ADD DECK</Text>
            </TouchableOpacity>
          </View>
              
          </View>
      </ScrollView>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
    resizeMode: 'cover', // or 'stretch' or 'contain'
    justifyContent: 'center',
  },
  scrollContainer: {
    flexGrow: 1,
    backgroundColor: 'rgba(0, 0, 0, 0)', // Set the background color to transparent
    alignItems: 'center',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
  },
  l1: {
    fontSize: 20,
    color: '#FFFFFF',
    marginBottom: 5,
  },
  l2: {
    fontSize: 12,
    marginBottom: 15,
    color: '#FFFFFF',
  },
  l3:{
    fontSize: 14,
    color: '#ECE3CE',
    marginLeft: -20,
    marginBottom: 5,

  },
  c2: {
    backgroundColor: '#ECE3CE',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10,
    padding: 20,
    position: 'relative',
    borderRadius: 20,
    opacity: 0.8,
  },
  c2l1: {
    fontSize: 16,
    marginRight: 140,
  },
  c2l2: {
    backgroundColor: '#4F6F52',
    paddingHorizontal: 10,
    paddingVertical: 10,
    borderRadius: 5,
    marginTop: 10,
    alignSelf: 'flex-start',
    borderRadius: 20,
    left: -5,
  },
  homechar: {
    width: 200,
    height: 220,
    position: 'absolute',
    top: 4,
    left: 180,
  },
  flashcardcontainer :{
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
  },
  deckTitle: {
    marginVertical: 15,
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  addButton: {
    marginTop: 20,
    padding: 10,
    backgroundColor: "green",
    borderRadius: "100%",
  }
});

export default App;
