import React from 'react';
import { ScrollView, StyleSheet, Text, TouchableOpacity, View } from 'react-native';

const App = () => {
  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <View style={styles.container}>
        <Text style={[styles.l1, { top: 20, left: 10 }]}>Hello, there!</Text>
        <Text style={[styles.l2, { top: 22, left: 10 }]}>What do you want to learn today?</Text>
        <View style={[styles.c2]}>
          <Text style={styles.c2l1}>Plant seeds of wisdom one flashcard at a time!</Text>
          <TouchableOpacity style={styles.c2l2}>
            <Text>Click me!</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
    backgroundColor: '#4F6F52',
    alignItems: 'center',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
  },
  l1: {
    fontSize: 20,
    color: '#FFFFFF',
    marginBottom: 5,
  },
  l2: {
    fontSize: 12,
    marginBottom: 15,
    color: '#FFFFFF',
  },
  c2: {
    backgroundColor: '#ECE3CE',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 70
  },
  c2l1: {
    fontSize: 14,
    marginRight: 10,
  },
  c2l2: {
    backgroundColor: '#007bff',
    paddingHorizontal: 10,
    paddingVertical: 50,
    borderRadius: 5,
  },
});

export default App;
