import React from 'react';
import { ImageBackground, ScrollView, StyleSheet, Text, TouchableOpacity, View, Image } from 'react-native';

const App = () => {
  return (
    <ImageBackground source={require('../assets/JungleBg.gif')} style={styles.backgroundImage}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <View style={styles.container}>

          <Text style={[styles.l1, styles.marginTop10]}>Hello, there!</Text>
          <Text style={[styles.l2, styles.marginTop10]}>What do you want to learn today?</Text>

          <View style={styles.c2}>
            <Text style={styles.c2l1} numberOfLines={2}>
              Plant seeds of wisdom {'\n'}one flashcard at a time!
            </Text>
            <TouchableOpacity style={styles.c2l2} onPress={handleCreateSetPress}>
              <Text style={styles.createSetText}>Create a study set now!</Text>
            </TouchableOpacity>
          </View>

          <Image style={styles.homechar} source={require('../assets/home_char.png')} />

          <View style={styles.flashcardcontainer} id='recent'>
            <Text style={[styles.l3, styles.marginTop5]}>Recently Viewed</Text>

            <FlatList
              data={decks}
              horizontal
              keyExtractor={(item) => item.id.toString()}
              renderItem={({ item }) => (
                <TouchableOpacity onPress={() => handleDeckPress(item)} style={styles.deckContainer}>
                  <Text style={styles.deckTitle}>{item.name}</Text>
                </TouchableOpacity>
              )}
            />

            <TouchableOpacity style={styles.addButton} onPress={handleAddDeckPress}>
              <Text style={styles.addButtonText}>ADD DECK</Text>
            </TouchableOpacity>

            <Text style={styles.deckTitle}>{selectedDeck.name}</Text>

            <Carousel
              layout="default"
              layoutCardOffset={9}
              ref={isCarousel}
              data={selectedDeck.flashcards}
              renderItem={({ item }) => (
                <Flashcard route={{ params: { frontContent: item.frontContent, backContent: item.backContent } }} />
              )}
              sliderWidth={SLIDER_WIDTH}
              itemWidth={ITEM_WIDTH}
              onSnapToItem={(index) => setIndex(index)}
              useScrollView={true}
            />

            <Pagination
              dotsLength={selectedDeck.flashcards.length}
              activeDotIndex={index}
              carouselRef={isCarousel}
              dotStyle={styles.paginationDot}
              inactiveDotOpacity={0.4}
              inactiveDotScale={0.6}
              tappableDots={true}
            />

            <TouchableOpacity style={styles.addButton} onPress={handleAddCardPress}>
              <Text style={styles.addButtonText}>ADD CARD</Text>
            </TouchableOpacity>
          </View>

        </View>
      </ScrollView>
    </ImageBackground>
  );
};


const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
    resizeMode: 'cover', // or 'stretch' or 'contain'
    justifyContent: 'center',
  },
  scrollContainer: {
    flexGrow: 1,
    backgroundColor: 'rgba(0, 0, 0, 0)', // Set the background color to transparent
    alignItems: 'center',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
  },
  l1: {
    fontSize: 20,
    color: '#FFFFFF',
    marginBottom: 5,
  },
  l2: {
    fontSize: 12,
    marginBottom: 15,
    color: '#FFFFFF',
  },
  l3:{
    fontSize: 14,
    color: '#ECE3CE',
    marginLeft: -20,
    marginBottom: 5,

  },
  c2: {
    backgroundColor: '#ECE3CE',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10,
    padding: 20,
    position: 'relative',
  },
  c2l1: {
    fontSize: 16,
    marginRight: 140,
  },
  c2l2: {
    backgroundColor: '#4F6F52',
    paddingHorizontal: 10,
    paddingVertical: 10,
    borderRadius: 5,
    marginTop: 10,
    alignSelf: 'flex-start',
  },
  homechar: {
    width: 200,
    height: 220,
    position: 'absolute',
    top: 4,
    left: 180,
  },
  flashcardcontainer :{
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
  },
});

export default App;
