import React from 'react';
import { Image, ScrollView, StyleSheet, Text, TouchableOpacity, View } from 'react-native';

const App = () => {
  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <View style={styles.container}>
        <Text style={[styles.l1, {top: 20}]}>Hello, there!</Text>
        <Text style={[styles.l2, {top: 20}]}>What do you want to learn today?</Text>
        <Image style={styles.homechar} source={require('../assets/home_char.png')} />
        <View style={[styles.c2]}>
          <Text style={styles.c2l1} numberOfLines={2}>Plant seeds of wisdom {'\n'}one flashcard at a time!</Text>
          <TouchableOpacity style={styles.c2l2}>
            <Text>Create a study set now!</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
    backgroundColor: '#4F6F52',
    alignItems: 'center',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
  },
  l1: {
    fontSize: 20,
    color: '#FFFFFF',
    marginBottom: 5,
  },
  l2: {
    fontSize: 12,
    marginBottom: 15,
    color: '#FFFFFF',
  },
  c2: {
    backgroundColor: '#ECE3CE',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 15,
    padding: 20,
    position: 'absolute',
    top: 70,
  },
  c2l1: {
    fontSize: 18,
    marginRight: 150,
  },
  c2l2: {
    backgroundColor: '#007bff',
    paddingHorizontal: 10,
    paddingVertical: 10,
    borderRadius: 5,
    marginTop: 10,
    alignSelf: 'flex-start',
  },
  homechar: {
    width: 200,
    height: 200,
    position: 'absolute',
    top: 20,
    left: 20
  }
});

export default App;
