import React from 'react';
import { ScrollView, StyleSheet, Text, TouchableOpacity, View } from 'react-native';

const App = () => {
  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <View style={styles.container}>
        <View style={styles.textContainer}>
          <Text style={[styles.l1, { top: 20, left: 10 }]}>Hello, there!</Text>
          <Text style={[styles.l2, { top: 60, left: 10 }]}>What do you want to learn today?</Text>
        </View>
        <TouchableOpacity style={styles.c2l2} onPress={() => console.log('Button Clicked!')}>
          <Text>Click me!</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  scrollContainer: {
    flexGrow: 1,
    backgroundColor: '#4F6F52',
    alignItems: 'center',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
  },
  textContainer: {
    marginTop: 20,
    marginLeft: 10,
  },
  l1: {
    fontSize: 20,
    color: '#FFFFFF',
    marginBottom: 5,
  },
  l2: {
    fontSize: 12,
    marginBottom: 15,
    color: '#FFFFFF',
  },
  c2l2: {
    backgroundColor: '#007bff',
    paddingHorizontal: 10,
    paddingVertical: 50,
    borderRadius: 5,
    marginTop: 10, // Adjust the margin as needed
  },
});

export default App;
